accessibleName.value:
{ $type ->
  [resistor] { $displayMode ->
               [name] Resistor
               [value] Resistor, { $resistance } ohms
               [count] Resistor { $position } of { $total }
               *[countAndValue] Resistor { $position } of { $total }, { $resistance } ohms
             }
  [battery] { $displayMode ->
              [name] Battery
              [value] Battery, { $voltage } volts
              [count] Battery { $position } of { $total }
              *[countAndValue] Battery { $position } of { $total }, { $voltage } volts
            }
  [lightBulb] { $displayMode ->
                [name] Light Bulb
                [value] Light Bulb, { $resistance } ohms
                [count] Light Bulb { $position } of { $total }
                *[countAndValue] Light Bulb { $position } of { $total }, { $resistance } ohms
              }
  [capacitor] { $displayMode ->
                [name] Capacitor
                [value] Capacitor, { $capacitance } farads
                [count] Capacitor { $position } of { $total }
                *[countAndValue] Capacitor { $position } of { $total }, { $capacitance } farads
              }
  [inductor] { $displayMode ->
               [name] Inductor
               [value] Inductor, { $inductance } henries
               [count] Inductor { $position } of { $total }
               *[countAndValue] Inductor { $position } of { $total }, { $inductance } henries
             }
  [acSource] { $displayMode ->
               [name] AC Voltage Source
               [value] AC Voltage Source, { $voltage } volts
               [count] AC Voltage Source { $position } of { $total }
               *[countAndValue] AC Voltage Source { $position } of { $total }, { $voltage } volts
             }
  [fuse] { $displayMode ->
           [name] Fuse
           [value] Fuse, { $resistance } milliohms ≤ currentRating amps
           [count] Fuse { $position } of { $total }
           *[countAndValue] Fuse { $position } of { $total }, { $resistance } milliohms ≤ currentRating amps
         }
  [switch] { $displayMode ->
             [name] { $switchState ->
                      [closed] Switch, closed
                      *[open] Switch, open
                    }
             [value] { $switchState ->
                       [closed] Switch, closed
                       *[open] Switch, open
                     }
             [count] { $switchState ->
                       [closed] Switch { $position } of { $total }, closed
                       *[open] Switch { $position } of { $total }, open
                     }
             *[countAndValue] { $switchState ->
                                [closed] Switch { $position } of { $total }, closed
                                *[open] Switch { $position } of { $total }, open
                              }
           }
  [voltmeter] { $hasPosition ->
                [true] Voltmeter { $position } of { $total }
                *[false] Voltmeter
              }
  [ammeter] { $hasPosition ->
              [true] Ammeter { $position } of { $total }
              *[false] Ammeter
            }
  [stopwatch] { $hasPosition ->
                [true] Stopwatch { $position } of { $total }
                *[false] Stopwatch
              }
  [wire] { $hasPosition ->
           [true] Wire { $position } of { $total }
           *[false] Wire
         }
  *[false] { $hasPosition ->
             [true] Wire { $position } of { $total }
             *[false] Wire
           }
}

accessibleHelpText.value:
{ $type ->
  *[switch] { $switchState ->
              [closed] This switch is closed. Press Space or Enter to open the switch.
              *[open] This switch is open. Press Space or Enter to close the switch.
            }
}
