terminalPrefix.value:
{ $terminalType ->
  [positiveTerminal] Positive Terminal of { $componentName }
  [negativeTerminal] Negative Terminal of { $componentName }
  [side] Side of { $componentName }
  [bottom] Bottom of { $componentName }
  *[none] { $componentName }
}

noChangesInGroup.value:
{ $showCurrent ->
  [true] { $hasLightBulbs ->
           [true] No changes in current or light bulb brightness in Group { $groupIndex }.
           *[false] No changes in current in Group { $groupIndex }.
         }
  *[false] { $hasLightBulbs ->
             [true] No changes in light bulb brightness in Group { $groupIndex }.
             *[false] No changes in Group { $groupIndex }.
           }
}
